import{_ as e,c as a,o as l,a4 as i}from"./chunks/framework.CZCMAXau.js";const _=JSON.parse('{"title":"Rust学习路线","description":"","frontmatter":{},"headers":[],"relativePath":"Document/Rust/Rust学习路线.md","filePath":"Document/Rust/Rust学习路线.md","lastUpdated":1713420998000}'),t={name:"Document/Rust/Rust学习路线.md"},r=i('<h1 id="rust学习路线" tabindex="-1">Rust学习路线 <a class="header-anchor" href="#rust学习路线" aria-label="Permalink to &quot;Rust学习路线&quot;">​</a></h1><p><a href="https://roadmap.sh/rust" target="_blank" rel="noreferrer">学习成为一名 Rust 开发人员 (roadmap.sh)</a></p><p><a href="https://mp.weixin.qq.com/s?__biz=MzUyODgxNzM0Nw==&amp;mid=2247485392&amp;idx=1&amp;sn=c97ad911e795de6f2325c1f12951a14d&amp;chksm=fb0464f2139fee870600bb58239d6851fd5725ea16d94c5009e564bf990eac33fe9dcb2c15ef&amp;scene=132&amp;exptype=timeline_recommend_article_extendread_samebiz&amp;show_related_article=1&amp;subscene=0&amp;scene=132#wechat_redirect" target="_blank" rel="noreferrer">第 N 次入门了，打算从头开始学 Rust。 (qq.com)</a></p><h2 id="rust语言基础" tabindex="-1">Rust语言基础 <a class="header-anchor" href="#rust语言基础" aria-label="Permalink to &quot;Rust语言基础&quot;">​</a></h2><ul><li>语法和语义</li><li>变量、常量和数据类型</li><li>控制流构造</li><li>函数和方法语法</li><li>模式匹配和解构</li></ul><h2 id="所有权" tabindex="-1">所有权 <a class="header-anchor" href="#所有权" aria-label="Permalink to &quot;所有权&quot;">​</a></h2><ul><li>所有权规则和内存安全</li><li>借用、引用和切片</li><li>深入理解栈与堆</li></ul><h2 id="错误处理" tabindex="-1">错误处理 <a class="header-anchor" href="#错误处理" aria-label="Permalink to &quot;错误处理&quot;">​</a></h2><ul><li>Option和Result枚举类型</li><li>错误传播和<code>?</code>运算符</li><li>自定义错误类型和特征</li></ul><h2 id="宏和元编程" tabindex="-1">宏和元编程 <a class="header-anchor" href="#宏和元编程" aria-label="Permalink to &quot;宏和元编程&quot;">​</a></h2><ul><li>声明式宏（macro_rules!）</li><li>过程宏和自定义派生</li><li>Rust 领域特定语言（DSLs）</li></ul><h2 id="rust的生态系统和库" tabindex="-1">Rust的生态系统和库 <a class="header-anchor" href="#rust的生态系统和库" aria-label="Permalink to &quot;Rust的生态系统和库&quot;">​</a></h2><ul><li>Web开发（如Rocket, Warp, Actix, Yew）</li><li>密码学（如ring, rust-crypto, sodiumoxide）</li><li>CLI工具（如clap, structopt, termion, rust-gdb, rust-lldb)</li><li>调试（如wasm-bindgen, wasm-pack, wasmer）</li><li>枚举、结构体、特征和实现块</li><li>数据结构（如向量、字符串、哈希映射、链表、二叉堆等）</li></ul><h2 id="高级主题" tabindex="-1">高级主题 <a class="header-anchor" href="#高级主题" aria-label="Permalink to &quot;高级主题&quot;">​</a></h2><ul><li>并发和并行编程（线程、通道、原子操作、内存屏障、Futures和Async/Await范式）</li><li>模块和包、代码组织和命名空间</li><li>依赖管理（Cargo.toml）和发布到crates.io</li><li>生命周期和借用检查器、特征和泛型（显式生命周期注释、生命周期省略规则、协变和逆变生命周期、特征定义和实现、特征界限和关联类型）</li><li>Tokio、async-std、smol等异步编程库</li><li>网络（如reqwest, hyper, quinn）</li><li>数据库和ORM（如Diesel, sqlx, rusqlite）</li><li>游戏开发（如bevy, fyrox, ggez, macroquad, wgpu-rs）</li><li>WebAssembly（WASM）</li><li>序列化/反序列化（如Serde, json-rust, toml-rs）</li><li>GUI开发（如druid, gtk-rs, relm）</li><li>嵌入式和系统编程（如embedded-hal, rppal, nrf-hal）</li><li>测试（单元测试和集成测试、模拟和基于属性的测试）</li><li>文档编写（rustdoc）</li><li>性能和分析（Criterion.rs）</li></ul><h2 id="持续学习" tabindex="-1">持续学习 <a class="header-anchor" href="#持续学习" aria-label="Permalink to &quot;持续学习&quot;">​</a></h2><p>这一点非常重要，应该要保持学习，和不断地探索和学习新特性及实践。这些路线图，可作参考来学习，其次参与社区讨论社群等，不懂就问，本来就是一个学习的过程。</p>',17),s=[r];function o(u,d,c,n,h,m){return l(),a("div",null,s)}const b=e(t,[["render",o]]);export{_ as __pageData,b as default};
